        public void fill(bill.ISBill i)
        {
            tb1.Text = i.Name;
            tb2.Text = $"{i.Cost}";
            tb3.Text = $"{i.Type}";
            tb.Text = "0".Equals(i.Desc) ? "" : i.Desc;

            if ( System.IO.File.Exists(i.Path) )
                pb.Image = Image.FromFile(i.Path);
        }
